        const set<ISO6392LanguageCode> & NGramsBased::languagesOf( const unicode::UTF32String & string,
                                                                   const size_t position )
        {
            letterNGrams::letterNGram<3> NGram =
                {string[position], string[position + 1], string[position + 2]};
            if (NGramsBased::NGramsIndex.count(NGram)) return NGramsBased::NGramsIndex[NGram];
            return NGramsBased::NoLanguage;
        }

        scores NGramsBased::actuallyIdentify( const tokenization::tokenizedString & tokens,
                                              const bool includeNonLinguisticScore )
        {
            if (tokens.empty()) return scores{};
            scoresMap byLanguage;
            size_t total = 0, nonActualWordsCount = 0, i, size;
            unicode::UTF32String buffer;
            for (const auto & token : tokens)
            {
                if (identification::isActualWord(token))
                {
                    buffer = letterNGrams::preExtrationString<1>(token);
                    for (i = 0, size = (buffer.size() - 3); i < size; i++, total++)
                    {
                        for (const auto & language : NGramsBased::languagesOf(token, i))
                            byLanguage[language] += 1;
                    }
                }
                else if ((!identification::isPartOfLanguage(token)) && (includeNonLinguisticScore))
                    byLanguage[ISO6392LanguageCode::zxx] += letterNGrams::count<3>(token);
            }
            total -= byLanguage[ISO6392LanguageCode::und];
            if (includeNonLinguisticScore)
            {
                total += byLanguage[ISO6392LanguageCode::zxx];
                identification::improveNonLanguageScore(byLanguage, nonActualWordsCount);
            }
            if (!total) return scores{};
            identification::removeUselessScores(byLanguage);
            scores results;
            results.reserve(byLanguage.size());
            double confidence;
            for (const auto & score : byLanguage)
            {
                confidence = score.second / total;
                if (confidence < minimumAcceptableConfidence) continue;
                results.push_back(std::make_pair(score.first, identification::roundedScore(confidence)));
            }
            std::sort(results.begin(), results.end(), identification::descendingScore);
            return results;
        }
